INSERT INTO dwh_report_extract (id, version, created, code, description, category, script_type, filename_format, result_type, uuid, sql_query) VALUES (-13, 0, NOW(), 'RECORDED_BA_HIERARCHY', 'Recorded per Billing Account with Hierarchy per month for interval, global filter', 'recorded', 'SQL', 'recorded_per_billing_account_hierarchy-[yyyyMMdd].csv', 'CSV', 'RECORDED_BA_HIERARCHY_UUID', 'SELECT trim(to_char(date_trunc(''month'', ao.invoice_date), ''Month'')) || to_char(date_trunc(''month'', ao.invoice_date), '' YYYY'') AS "Month"
