    ,to_char(sum(CASE 
                WHEN ao.transaction_category = ''CREDIT''
                    THEN ao.amount
                ELSE 0
                END), ''99999999990.00'') AS "Amount CREDIT"
    ,to_char(sum(CASE 
                WHEN ao.transaction_category = ''DEBIT''
