INSERT INTO dwh_report_extract (id, version, created, code, description, category, script_type, filename_format, result_type, uuid, sql_query) VALUES (-16, 0, NOW(), 'RECORDED_C', 'Recorded per Customer per month for interval, global filter', 'recorded', 'SQL', 'recorded_per_customer-[yyyyMMdd].csv', 'CSV', 'RECORDED_C_UUID', 'SELECT trim(to_char(date_trunc(''month'', ao.invoice_date), ''Month'')) || to_char(date_trunc(''month'', ao.invoice_date), '' YYYY'') AS "Month"
